import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Header = () => {
  let navigate = useNavigate();
  let location = useLocation();

  const [activeClass, setActiveClass] = useState("nav-link mx-5");
  const [activeClass2, setActiveClass2] = useState("nav-link");

  const switchSet2 = async () => {
    navigate(`/set2`);
  };
  const switchSet1 = async () => {
    navigate(`/`);
  };

  //Giving the "active" to the Nav-bar link depending on the page we're on
  useEffect(() => {
    const activeSet = () => {
      if (location.pathname === "/") {
        setActiveClass("nav-link active mx-5");
      } else {
        setActiveClass2("nav-link active");
      }
    };
    activeSet();
  }, [location.pathname]);

  return (
    <div className="mb-4">
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
        <div className="container-fluid">
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNavAltMarkup"
            aria-controls="navbarNavAltMarkup"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <button
                type="button"
                className={activeClass}
                onClick={() => switchSet1()}
              >
                Data Set 1
              </button>
              <button
                type="button"
                className={activeClass2}
                onClick={() => switchSet2()}
              >
                Data Set 2
              </button>
            </div>
          </div>
        </div>
      </nav>

      <ul className="nav --bs-info-bg-subtle justify-content-center">
        <li className="nav-item">
          <button className="nav-link">
            Dataset 1
          </button>
        </li>
        <li className="nav-item">
          <button className="nav-link">
            Dataset 2
          </button>
        </li>
      </ul>
    </div>
  );
};

export default Header;
