import React, { useEffect, useState } from "react";
import axios from "axios";

const ListSetTwo = () => {

    const [psets, setPsets] = useState([]);

    //helper function for sort method to compare names within object
    const compareName = (a, b) => {
      if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
      else if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
      return 0;
    };
  
    //helper function for sort method to compare dates within object
    const compareDate = (a, b) => {
      if (a.info.date.created > b.info.date.created) return 1;
      else if (a.info.date.created < b.info.date.created) return -1;
      return 0;
    }
  
    const filterChrono = () => {
      let psetsCopy = [...psets];
      psetsCopy.sort(compareDate);
      setPsets(psetsCopy);
    }
  
    const filterAlpha = () => {
      let psetsCopy = [...psets];
      psetsCopy.sort(compareName);
      setPsets(psetsCopy);
    };

    useEffect(() => {
      const fetch = async () => {
        try {
          const response = await axios.get("http://localhost:3006/api/pset-database");
          setPsets(response.data);
          console.log(response.data)
        } catch (error) {
          console.log(error);
        }
      };
  
      fetch();
    }, []);
    return (
      <div className="container">
        <div className="text-center my-5">
          <button type="button" className ="btn btn-primary mx-5" onClick={filterAlpha}>Filter Alphabetically</button>
          <button type="button" className ="btn btn-primary" onClick={filterChrono}>Filter Chronologically</button>
        </div>
        <table className="table">
          <thead>
            <tr>
              <th scope="col"></th>
              <th scope="col">Name</th>
              <th scope="col">Date Created</th>
              <th scope="col">Doi</th>
            </tr>
          </thead>
          <tbody>
            {psets && psets.map((pset, index) => {
                let doiLink = `https://doi.org/${pset.repositories[0].doi}`;
                return (
                  <tr key={pset.repositories[0].doi}>
                    <th scope="row">{index + 1}</th>
                    <td>{pset.name}</td>
                    <td>{pset.info.date.created.slice(0, 10)}</td>
                    <td>
                      <a href={doiLink}>{doiLink}</a>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    );
  };

export default ListSetTwo
