import React, { useEffect, useState } from "react";
import axios from "axios";

const List = () => {
  const [psets, setPsets] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get("http://localhost:3006/api/psets");
        console.log(response.data);
        setPsets(response.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetch();
  }, []);
  return (
    <div className="container">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">Name</th>
            <th scope="col">Doi</th>
          </tr>
        </thead>
        <tbody>
          {psets && psets.map((pset, index) => {
              let doiLink = `https://doi.org/${pset.doi}`
              console.log(doiLink)
              return (
                <tr key={pset.doi}>
                  <th scope="row">{index + 1}</th>
                  <td>{pset.name}</td>
                  <td><a href={doiLink}>{doiLink}</a></td>
                </tr>
              );
            })}
        </tbody>
      </table>
    </div>
  );
};

export default List;
