import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Header = () => {

  let navigate = useNavigate();
  let location = useLocation()

  const [activeClass, setActiveClass] = useState("nav-link mx-5")
  const [activeClass2, setActiveClass2] = useState("nav-link")

  const switchSet2 = async () => {
    navigate(`/set2`);
  };
  const switchSet1 = async () => {
    navigate(`/`);
  };

  useEffect(() =>{
    const active = () =>{
      if (location.pathname === "/"){
        setActiveClass("nav-link active mx-5")
      }
      else{
        setActiveClass2("nav-link active")
      }
    }

    active()
  }, [])


  return (
    <div className="">
      <nav className="navbar navbar-expand-lg bg-body-tertiary center-text">
        <div className="container-fluid ">
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNavAltMarkup"
            aria-controls="navbarNavAltMarkup"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <button
                type="button"
                className={activeClass}
                onClick={() => switchSet1()}
              >
                Data Set 1
              </button>
              <button
                type="button"
                className={activeClass2}
                onClick={() => switchSet2()}
              >
                Data Set 2
              </button>
            </div>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Header;
